INSERT INTO "Pages" VALUES (1, 'Blue Mountains', 'http://apps.visitnsw.com/smartphoneapps/blue-mountains', 1410401057000, '', '{"Page":{"Title":"Blue Mountains","Content":"\r\n<div id=\"content_div_175870\">\r\n<div id=\"content_div_133882\" style=\"font-family: Arial, Verdana, Helvetica, sans-serif; font-size: 11px;\">\r\n  <span style=\"color: rgb(51, 51, 51); font-family: arial, helvetica, sans-serif; font-size: 12px; line-height: 15.984375px;\">A trip to New South Wales would not be complete without a visit to the Blue Mountains. Less than two hours from Sydney by road or train, the&nbsp;<\/span><a href=\"http:\/\/www.visitnsw.com\/things-to-do\/nature-and-parks\/world-heritage-nsw\" style=\"color: rgb(51, 51, 51); outline: none; font-family: arial, helvetica, sans-serif; font-size: 12px; line-height: 15.984375px;\">World Heritage listed Blue Mountains<\/a><span style=\"color: rgb(51, 51, 51); font-family: arial, helvetica, sans-serif; font-size: 12px; line-height: 15.984375px;\">&nbsp;are one of the country\u2019s most spectacular icons. With picturesque villages, breathtaking views, and so much to see and do, the Blue Mountains are the perfect holiday destination for families, nature lovers, and adventure seekers.<\/span><br \/>\r\n\r\n  <table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" class=\"bodytext\"><\/table>\r\n<\/div>\r\n\r\n<\/div>\r\n"},"Children":[{"Name":"Home","Url":"http:\/\/apps.visitnsw.com\/smartphoneapps\/blue-mountains\/home"},{"Name":"About Blue Mountains","Url":"http:\/\/apps.visitnsw.com\/smartphoneapps\/blue-mountains\/about"},{"Name":"Local Areas","Url":"http:\/\/apps.visitnsw.com\/smartphoneapps\/blue-mountains\/local-areas"},{"Name":"Things to do","Url":"http:\/\/apps.visitnsw.com\/smartphoneapps\/blue-mountains\/things-to-do"},{"Name":"Dining &amp; Entertainment","Url":"http:\/\/apps.visitnsw.com\/smartphoneapps\/blue-mountains\/dining-and-entertainment"},{"Name":"Transport Information","Url":"http:\/\/apps.visitnsw.com\/smartphoneapps\/blue-mountains\/transport-information"},{"Name":"About Destination NSW","Url":"http:\/\/apps.visitnsw.com\/smartphoneapps\/blue-mountains\/about-destination-nsw"}]}');
