INSERT INTO "Pages" VALUES (56, 'About Destination NSW', 'http://www.destinationnsw.com.au/smartphoneapps/central-coast/about-destination-nsw', 1390517680000, '', '{"Page":{"Title":"About Destination NSW","Content":"\r\n<div id=\"content_div_215742\">\r\n<p>DNSW is responsible for devising and implementing strategies to grow the visitor economy, with particular focus on driving tourism and acquiring and developing major sporting and cultural events for Sydney and regional NSW. In addition, DNSW is the major investor in Business Events Sydney with the aim to secure more international conventions, incentive travel reward programs, corporate events and exhibitions for Sydney and regional NSW.<\/p>\r\n\r\n<\/div>\r\n"},"Children":[{"Name":"About this App","Url":"http:\/\/www.destinationnsw.com.au\/smartphoneapps\/central-coast\/about-destination-nsw\/about-this-app"}]}');
